import logo from './logo.svg';
import './App.css';
import React, { useState, useEffect } from "react";
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet'
import { Icon } from 'leaflet'
import covidData from './data.json'
import icon1 from './images/covid19.svg'
// import { LatLngExpression } from 'leaflet';
import { divIcon } from 'leaflet';


function App() {
  const [ activeInfo, setActiveInfo ] = useState( null );

  const [position, setPosition] = useState([0,0])
  const [serverResponse, setServerResponse] = useState()
  const [featuresArray, setFeaturesArray] = useState()
  async function getData() {
    console.log("fetching data")
    const response = await fetch('http://localhost:5000/mapview');
    console.log("fetching data in json")
    const jsonResponse = await response.json();
    const teste = jsonResponse.features
    console.log(teste, '<<<< thats what I am getting from server ')
    // var jsonCoordinates = jsonResponse.features[0].geometry.coordinates
    var featuresArray = jsonResponse.features.slice(1,5)    // todo remove slice!

    // console.log(jsonCoordinates, '<<<< final coordinates x and y ')
    // setServerResponse(jsonCoordinates)
    setFeaturesArray(featuresArray)
  }

  useEffect(() => {
    console.log("in use effect!")
    getData();
  }, []);

  const [ activeCovid, setActiveCovid ] = useState( null );

  const myCustomColour = '#583470'

  const markerHtmlStyles = `
    background-color: ${myCustomColour};
    width: 3rem;
    height: 3rem;
    display: block;
    left: -1.5rem;
    top: -1.5rem;
    position: relative;
    border-radius: 3rem 3rem 0;
    transform: rotate(45deg);
    border: 1px solid #FFFFFF`

  const icon = Leaflet.divIcon({
    className: "my-custom-pin",
    iconAnchor: [0, 24],
    labelAnchor: [-6, 0],
    popupAnchor: [0, -36],
    html: `<span style="${markerHtmlStyles}" />`
  })




  return (<>
 <MapContainer 
          center = {[8.382046, 11.6146463 ]}
          center={position} 
            zoom = { 1 }
          scrollWheelZoom = { true } 
      >
      <TileLayer 
        attribution='&copy; <a href="https://stadiamaps.com/">Stadia Maps</a>, &copy; <a href="https://openmaptiles.org/">OpenMapTiles</a> &copy; <a href="http://openstreetmap.org">OpenStreetMap</a> contributors'
        url = 'https://tiles.stadiamaps.com/tiles/alidade_smooth_dark/{z}/{x}/{y}{r}.png'
      />
       <TileLayer 
          attribution = '&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors' 
          url = "https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" 
        />
   {featuresArray && featuresArray.map((featureObject, idx1) => { 
     return featureObject.geometry.coordinates.map((singleCoordinate, idx2) => {
      const myid = idx1 + '_' + idx2 
       return (<Marker       key={myid}  position={singleCoordinate}
        icon= {icon}

       > 


       </Marker>)

     }
     )})} 

  </MapContainer> 

  </>
  );
}

export default App;
