import logo from './logo.svg';
import './App.css';
import React, { useState, useEffect } from "react";
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet'
import { Icon } from 'leaflet'
import covidData from './data.json'
import icon1 from './images/covid19.svg'

function App() {
  //saving the information to be able to render
  const [serverResponse, setServerResponse] = useState()
  const [ activeInfo, setActiveInfo ] = useState( null );

// serverResponse = value
//setServerResponse is a function with the value as parameter
  async function getData() {
    console.log("fetching data")
    const response = await fetch('http://localhost:5000/mapview');
    console.log("fetching data in json")
    const jsonResponse = await response.json();
    // setServerResponse(jsonResponse)
    console.log("fetching the json FEATURES")
    var jsonFeatures = jsonResponse.features
    console.log(jsonFeatures, 'NOW JSON FEATURES IN AN ARRAY')
    setServerResponse(jsonFeatures)
  }

  useEffect(() => {
    console.log("in use effect!")
    getData();
  }, []);
  // const [ activeCovid, setActiveCovid ] = useState( null );


  return (<>


 <MapContainer 
          center = { [ 20.593683, 78.962883 ] }
          zoom = { 5 }
          scrollWheelZoom = { true } 

      >
      <TileLayer 
        attribution='&copy; <a href="https://stadiamaps.com/">Stadia Maps</a>, &copy; <a href="https://openmaptiles.org/">OpenMapTiles</a> &copy; <a href="http://openstreetmap.org">OpenStreetMap</a> contributors'
        url = 'https://tiles.stadiamaps.com/tiles/alidade_smooth_dark/{z}/{x}/{y}{r}.png'
      />


       <TileLayer 
          attribution = '&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors' 
          url = "https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" 
        />
  {serverResponse && <Marker>
  {serverResponse.map((anObjectMapped, index) => {
    return (
        <p key={index}>
            {anObjectMapped.geometry.coordinates[index]}
        </p>
    );
})}

  </Marker>}

  { serverResponse && (
        <Popup 
          position={ [ serverResponse.geometry.coordinates[0], serverResponse.geometry.coordinates[0]] }
          // onClose={()=>{
          //   setActiveInfo(null)
          // }}
        >
          <div>
            {/* <h1>{ activeCovid.Location }</h1> */}
            {/* <p>Total cases:                { activeCovid.Total_Cases }</p>
            <p>New cases (1 day*):         { activeCovid.New_Cases_Per_Day }</p>
            <p>Cases per 1 million people: { activeCovid.Cases_Per_1_Million_People }</p>
            <p>Deaths:                     { activeCovid.Deaths }</p> */}
          </div>
        </Popup> )}

  </MapContainer> 

  </>
  );
}

export default App;
